import "../../App.css"
import Header from "../../components/Header"
import {Link} from 'react-router-dom'
import Navigation from "../../components/Navigation";
import Footer from "../../components/Footer";
import ApiCustomerService from "../../services/customer/ApiCustomerService";
import React, { Component } from 'react'
import Swal from "sweetalert2";
const validEmailRegex = RegExp(
  /^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i
);
const validateForm = errors => {
  let valid = true;
  Object.values(errors).forEach(val => val.length > 0 && (valid = false));
  return valid;
};
class CreateAccountScreen extends Component {

  constructor(props) {
    super(props)
    this.state ={
      firstName: '',
      lastName: '',
      email: '',
      password: '',
      phone: '',
      role:'CUSTOMER',
      message: '',
      errors: {
        firstName: '',
        lastName : '',
        email: '',
        password: '',
      }
  }
    this.registerUser = this.registerUser.bind(this);
   
}

handleChange = (event) => {
  event.preventDefault();
  const { name, value } = event.target;
  let errors = this.state.errors;

  switch (name) {
    case 'fullName': 
      errors.firstName = 
        value.length < 2
          ? 'First Name must be at least 2 characters long!'
          : '';
      break;
      case 'lastName': 
      errors.lastName = 
        value.length < 4
          ? 'Last Name must be at least 4 characters long!'
          : '';
      break;
    case 'email': 
      errors.email = 
        validEmailRegex.test(value)
          ? ''
          : 'Email is not valid!';
      break;
    case 'password': 
      errors.password = 
        value.length < 8
          ? 'Password must be at least 8 characters long!'
          : '';
      break;
    default:
      break;
  }

  this.setState({errors, [name]: value});
}

  handleSubmit = (event) => {
    event.preventDefault();
    if(validateForm(this.state.errors)) {
      console.info('Valid Form')
    }else{
      console.error('Invalid Form')
    }
  }

onChange = (e) =>
        this.setState({ [e.target.name]: e.target.value });

    registerUser = (e) => {
      e.preventDefault();
      let user = {firstName: this.state.firstName, lastName: this.state.lastName, email: this.state.email, password: this.state.password, phone: this.state.phone, role: this.state.role};
      ApiCustomerService.addUser(user)
          .then(res => {
            if(res.data.result === null){
            //  alert("Email Addreess Already Registered")
              Swal.fire({
                icon: 'success',
                title: 'Email Address Already Registered',
                showConfirmButton: true,
                confirmButtonText: 'OKAY',
              })
            
            }
            if(res.data.result !== null){
            //  alert("SignUp successfully")
              Swal.fire({
                icon: 'success',
                title: 'User Registered',
                showConfirmButton: true,
                confirmButtonText: 'OKAY',
              })
              this.setState({message : 'SignUp successfully.'});
              this.props.history.push('/login');
            }
            
          });
  }

  render(){
    const {errors} = this.state;
    return (
      <div>
        <Navigation/>
        <div className="main">
      <Header title="Create Account" />
      <div className="form">
      <div className="row mb-3">
          <label htmlFor="firstName" className="col-sm-4 col-form-label">First Name</label>
          <div className="col-sm-8">
              <input type="text" className="form-control" name="firstName" value={this.state.firstName} onChange={this.handleChange} noValidate/>
          </div>
       </div>

       <div className="row mb-3">
          <label htmlFor="lastName" className="col-sm-4 col-form-label">Last Name</label>
          <div className="col-sm-8">
              <input type="text" className="form-control" name="lastName" value={this.state.lastName} onChange={this.handleChange}/>
          </div>
       </div>

       <div class="row mb-3">
          <label className="col-sm-4 col-form-label">Email</label>
          <div className="col-sm-8">
              <input type="email" className="form-control" name="email" value={this.state.email} onChange={this.handleChange}/>
          </div>
       </div>

       <div className="row mb-3">
          <label className="col-sm-4 col-form-label">Password</label>
          <div className="col-sm-8">
              <input type="password" className="form-control" name="password" value={this.state.password} onChange={this.handleChange}/>
          </div>
       </div>

       <div className="row mb-3">
          <label className="col-sm-4 col-form-label">Phone</label>
          <div className="col-sm-8">
              <input type="text" className="form-control" name="phone" value={this.state.phone} onChange={this.handleChange}/>
          </div>
       </div>
        <div className="mb-3">
        <div className="float-start"><br></br>
            Existing User? <Link to="/login">Login here</Link>
          </div>
          <button className="btn-hover color-9 float-end" onClick={this.registerUser}>
            Register
          </button>
          <br></br>

        </div>
      </div>
    </div>
      </div>
  )
  }
    
}
export default CreateAccountScreen
