import {Link} from 'react-router-dom'
import React, { Component } from 'react'
import Navigation from "../../components/Navigation";
import Footer from "../../components/Footer"; 
import ApiCustomerService from "../../services/customer/ApiCustomerService";
import Swal from 'sweetalert2';
class CartScreen extends Component{

    constructor(props) {
        super(props)
        this.state = {
        cart:[],
        st : false,
        tamt : 0,
        samt : 0,
        sts:'Cart Is Empty',
      }
        this.getStatus = this.getStatus.bind(this);
        this.placeOrder = this.placeOrder.bind(this);
        this.deleteProduct = this.deleteProduct.bind(this);
        this.addAddress = this.addAddress.bind(this);
    }
    showProfile(){
        this.props.history.push('/customer/myaccount/profile');
    }
    showCategory(){
      this.props.history.push('');
      
  }
  showCart(){
      this.props.history.push('/cart');
      
  }
    editProfile(){
        this.props.history.push('/myaccount/editprofile');
    }
    changePassword(){
        this.props.history.push('/myaccount/change-password');
    }
    showOrderHistory(){
        this.props.history.push('/myaccount/orderhistory');
    }
    showdeliveredOrders(){
        this.props.history.push('/deliveredorderforadmin');
    }
    showSupplier(){
        this.props.history.push('/showsupplier');
    }
    showDeliveryBoy(){
        this.props.history.push('/showdeliveryboy');
    }
    logout(){
        this.props.history.push('/logout');
    }
    componentDidMount() {
      this.getStatus();
      let size = JSON.parse(window.localStorage.getItem("cart_size"))
      if(size !== 0){
        ApiCustomerService.getCartByUserId(JSON.parse(window.localStorage.getItem("user_id")))
        .then((res) => {
            this.setState({cart : res.data.result})
        });

        ApiCustomerService.getTAmtUserId(JSON.parse(window.localStorage.getItem("user_id")))
        .then((res) => {
            this.setState({tamt : res.data.result})
        });

        ApiCustomerService.getSAmtByUserId(JSON.parse(window.localStorage.getItem("user_id")))
        .then((res) => {
            this.setState({samt : res.data.result})
        });
    }
      
    }

    getStatus() {
    this.setState(prevState => ({ st: window.localStorage.getItem("status") == 'true'}))
    }

    placeOrder() {
        let size = JSON.parse(window.localStorage.getItem("cart_size"))
        if(size == 0){
            //alert(" !!! Cart Is Empty !!!")
            Swal.fire({
                icon: 'success',
                title: '!!! Cart Is Empty !!!',
                showConfirmButton: true,
                confirmButtonText: 'OKAY',
              })
        }
        if(size !== 0){
            window.localStorage.setItem("add", this.state.pinCode)
            if(this.state.st && window.localStorage.getItem("addressStatus") == 'false'){
              //  alert(" !!! Enter Valid Address !!!")
                Swal.fire({
                    icon: 'success',
                    title: '!!! Enter Valid Address !!!',
                    showConfirmButton: true,
                    confirmButtonText: 'OKAY',
                  })
            }
            !this.state.st && this.props.history.push('/login');
            if(window.localStorage.getItem("addressStatus") == 'true'){
                window.localStorage.setItem("addressStatus", false)
                this.state.st && this.props.history.push('/payment');
            !this.state.st && this.props.history.push('/login');
            window.localStorage.setItem("total_price", this.state.tamt)
            } 
                 
        }
        
    }

    addAddress() {
        let size = JSON.parse(window.localStorage.getItem("cart_size"))
        if(size == 0){
           // alert(" !!! Cart Is Empty !!!")
            Swal.fire({
                icon: 'success',
                title: '!!! Cart Is Empty !!!',
                showConfirmButton: true,
                confirmButtonText: 'OKAY',
              })
        }
        if(size !== 0){
            this.state.st && window.localStorage.setItem("addressStatus", true)
            !this.state.st && this.props.history.push('/login');
            this.state.st && this.props.history.push('/orderaddress');
            }
        }
        
    deleteProduct(cartId, qty) {
        Swal.fire({
            title: 'Are you sure?',
            text: "You won't be able to revert this!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!'
          }).then((result) => {
            if (result.isConfirmed) {
              Swal.fire({
                icon : 'success',
                title : 'Deleted!',
                text : 'Your file has been deleted.',
                showConfirmButton: true,
                confirmButtonText: 'OKAY',
              })
              ApiCustomerService.deleteCartByUserId(cartId)
            }
          })
        
        .then((res) => {
            window.location.reload();
            JSON.stringify(window.localStorage.setItem("cart_size", JSON.parse(window.localStorage.getItem("cart_size")) - qty));        
        });
       
    }

  render() {
    return (
       <div>
           <Navigation/>
              <div className="container">
              <div className="main3">
            <table>
                <td><button className="btn4 btn-success" onClick={() => this.showProfile()}>Profile</button></td>
               
                <td><button className="btn4 btn-success" onClick={() => this.showCategory}>Category</button></td>
                <td><button className="btn4 btn-success" onClick={() => this.showOrderHistory()}>Order history</button></td>
                <td><button className="btn4 btn-success" onClick={() => this.showCart()}>Cart</button></td>
                <td><button className="btn4 btn-danger" onClick={() => this.logout()}>Logout</button></td>
            </table>
        </div>
                <h3 className='text-center'>Cart : </h3>
                <div className="cart1">
                    <table className="table table-striped">
                    <thead>
                        <tr className="float-center">
                            <th>Product Name</th>
                            <th>Quantity</th>
                            <th>Price</th>
                            <th>Total</th>
                            <th>Delete</th>
                        </tr>
                        </thead>
                        <tbody>
                        <div className="container"><h5 className="nameColor1">{this.state.cart.length === 0 && this.state.message}</h5></div>
                        {this.state.cart.map(
                            product => 
                            <tr key={product.id}>
                                <td>{product.productName}</td>
                                <td>{product.qty}</td>
                                <td>{product.finalPrice}</td>
                                <td>{product.finalPrice * product.qty}</td>
                                <td><button className="btn4 btn-danger" onClick={() => this.deleteProduct(product.id, product.qty)}>Delete</button></td>
                            </tr>
                        )}
                        </tbody>
                    </table>
                </div>
                <div className="cart1">
                    <h3>Total Amount : {this.state.tamt}</h3>
                    
                    <h3>Grand Total : {this.state.tamt + this.state.samt}</h3>
                </div>
                </div>
        <br/>

        <div className="float-end">
        <h5>Total Price : {this.state.tamt}</h5>
        <h5>Savings Amount : {this.state.samt}</h5>
        <button className="btn4 btn-primary" style={{width:'200px'}} onClick={() => this.addAddress()}>Add Address</button><br/><br/>
        <button className="btn4 btn-primary" style={{width:'200px'}} onClick={() => this.placeOrder()}>Place Order</button>
        </div>
        <br/>
        <br/>
        <br/>
        <br/>
        <br/>
        <br/>
    </div>
       
    );
    }
}
export default CartScreen
